mapper.select:
SELECT u.user_name AS user_name,u.user_pwd AS user_pwd,u.salt AS salt, u.user_email AS user_email,u.user_phone AS user_phone,u.nick_name AS nick_name,u.user_sex AS user_sex,u.activated_state AS activated_state,u.save_time AS save_time,u.reset_pwd AS reset_pwd,u.id AS user_id, MAX(l.login_time) AS login_time , r.role_name AS user_level_show,r.id AS user_level FROM djmall_auth_user u LEFT JOIN user_login l ON u.id = l.user_id LEFT JOIN djmall_auth_user_role ur ON u.id = ur.user_id LEFT JOIN djmall_auth_role r ON r.id = ur.role_id where 1 = 1
AND (u.user_name like "%" #{userBo.userName} "%" or u.user_phone like "%" #{userBo.userName} "%" or u.user_email like "%" #{userBo.userName} "%")
AND u.user_sex= #{userBo.userSex}
AND ur.role_id= #{userBo.userLevel}
AND u.activated_state= #{userBo.activatedState}
GROUP BY u.id DESC
