mapper.update:
UPDATE djmall_product_spu spu
JOIN djmall_product_sku sku ON spu.id = sku.product_id
SET sku.sku_status = #{status},
        spu.`status` = #{status}
WHERE spu.id = #{id}

mapper.select:
select spu.*,spu.id AS productId,us.user_name AS userName,bd.name AS name,log.freight AS freight from djmall_product_spu spu LEFT JOIN djmall_auth_base_data bd ON bd.`code` = spu.type LEFT JOIN djmall_auth_user us ON us.id = spu.user_id LEFT JOIN djmall_dict_product_logistics log ON spu.freight_id = log.id where 1 = 1
AND spu.product_name like "%"#{productName}"%"
AND spu.type= #{type}
